import React from 'react';
import '../../css/tweets/TweetSection.css';
import Tweet from './tweet.js';
import TweetEdit from './tweetEdit.js';

class TweetSection extends React.Component {
    constructor(props) {
        super(props);
        this.tweets = props.tweets.map(tweet => <Tweet tweet={tweet} twitterAccounts={this.props.twitterAccounts} />); 

    }

    

    render() {
        return (

            <div id="TweetsContainer">
                {this.tweets}
                 

                
                
            </div>
            
        );
    }
}

export default TweetSection;
