import React from 'react';
import '../../css/submenu/UnlinkAccountsMenu.css';
import './unlinkAccountConfirmMenu.js';
import Account from '../account.js';
import AccountList from '../accountList.js';

class UnlinkAccountsMenu extends React.Component {
    constructor(props) {
        super(props);

        let unlinkAccountMenuItemsHelper = props.twitter_accounts.map((twitterAccount) => { return { account_id: twitterAccount.account_id, selected: false } })

        this.state = {
            menuState: 'Select',
            unlinkAccountMenuItems: unlinkAccountMenuItemsHelper
        };

        this.handleUnlinkAccountClick = this.handleUnlinkAccountClick.bind(this);
    }


    handleUnlinkAccountClick(event) {
        if (this.props.selectedAccounts().length) this.props.changeSubmenuState(event);
    }


    render() {
        return (
            <div className="UnlinkAccountsMenu">
                <AccountList accounts={this.state.unlinkAccountsMenuItems} onSelect={this.props.select} />
                <div className="BtnContainer">
                    <div className="SubmenuBtn" id="Confirm Unlink Account" onClick={this.handleUnlinkAccountClick}>Unlink Account</div>
                    <div className="SubmenuBtn" id="Cancel" onClick={this.props.changeSubmenuState}>Cancel</div>
                </div>
            </div>
        );
    }
}


export default UnlinkAccountsMenu;
