import React from 'react';
import '../../css/tweets/TweetDisplayMenu.css';

class TweetDisplayMenu extends React.Component {
    constructor(props) {
        super(props);
        this.handleChangeTweetState = this.handleChangeTweetState.bind(this);
    } 

    handleChangeTweetState(newTweetState) {
        this.props.onChangeTweetState(newTweetState);
    }

    render() {

        return (
            <div className="TweetBtnContainer">
                <div className="TweetBtn" onClick={this.handleChangeTweetState('Edit')}>DELETE</div>
                <div className="TweetBtn" onClick={this.handleChangeTweetState('Delete')}>EDIT</div>
            </div>
            );
    }
}

export default TweetDisplayMenu;
