import React from 'react';
import '../../css/submenu/DeleteAccountConfirmMenu.css';
import Account from '../account.js';

class DeleteAccountConfirmMenu extends React.Component {
    constructor(props) {
        super(props);
    }

    
    handleDelete() {
        fetch('/deleteUserAccount', { method: 'POST' });

        let expirationDate = new Date();

        this.props.onLogout('');

        document.cookie = "username=; domain=localhost:3002; expires=" + expirationDate.toUTCString();
        document.cookie = "connect.sid=; domain=localhost:3002; expires=" + expirationDate.toUTCString();    
    }

    render() {
        return (
            <div className="DeleteAccountConfirmMenu">
                <h3>Are you sure you want to delete this account?</h3>
                <div className="AccountContainer">
                    <Account name={this.props.username} selected={true}/>
                </div>
                <div className="BtnContainer">
                    <div className="SubmenuBtn">Confirm</div>
                    <div className="SubmenuBtn" id="Cancel" onClick={this.props.changeSubmenuState}>Cancel</div>

                </div>
            </div>
        );
    }
}

export default DeleteAccountConfirmMenu;
