import React from 'react';
import '../../css/tweets/TweetTextForm.css';

class TweetTextForm extends React.Component {
    constructor(props) {
        super(props);
        this.handleChange = this.handleChange.bind(this);

        this.state = {
            tweet_text: props.tweet_text
        }
    }


    handleChange(event) {
        this.setState({ tweet_text: event.target.value });
    }

    render() {

        return (
            <textarea name="tweetText" style={{ height: '80px' }} className="TweetTextEdit" value={this.state.tweet_text} onChange={this.handleChange}></textarea>
        );
    }
}

export default TweetTextForm;
