import React from 'react';
import '../../css/tweets/TweetEdit.css';
import TweetTitleForm from './tweetTitleform';
import TwitterAccountForm from './twitterAccountForm';
import TweetTextForm from './tweetTextForm.js';
import TweetEditMenu from './tweetEditMenu.js';
import TweetForm from './tweetForm.js';


class TweetEdit extends React.Component {
    constructor(props) {
        super(props);

        this.handleReturnedEditedTweet = this.handleReturnedEditedTweet.bind(this);
        this.cancel = this.cancel.bind(this);

        this.state = {
            formOptions: {
                tweet: Object.assign({}, props.tweet),
                twitterAccounts: props.twitterAccounts,                
                submitRequestUrl: '/editTweet/' + props.tweet.post_id,
                handlePostSubmit: this.postCreateTweetFormSubmit,
                onCancel: this.cancel
            }
        };

        
     
    }

    handleReturnedEditedTweet(response) {
        response.json().then((data) => this.props.refreshTweet(data));
    }

    cancel() {
        this.props.onChangeTweetState("Display");
    }


    render() {

        return ( 
            <TweetForm formOptions={this.state.formOptions} />
        );
    }
}

export default TweetEdit;
