import React from 'react';
import '../../css/tweets/TweetEdit.css';
import TweetTitleForm from './tweetTitleform';
import TwitterAccountForm from './twitterAccountForm';
import TweetTextForm from './tweetTextForm.js';
import TweetEditMenu from './tweetEditMenu.js';


class TweetEdit extends React.Component {
    constructor(props) {
        super(props);
        
        this.state = {
            tweetEdits: Object.assign({}, this.props.tweet)
        }

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {

        this.editedTweet = Object.assign({}, this.state.tweetEdits);

        this.propertyEdit = event.target.type;

        switch (this.propertyEdit) {

            case 'text':
                this.editedTweet.tweet_title = event.target.value;
                break;

            case 'select-one':
                this.editedTweet.account_id = event.target.value;
                break;

            case 'textarea':
                this.editedTweet.tweet_text = event.target.value;
                break;
            }

        this.setState({
            tweetEdits: Object.assign({}, this.editedTweet)
        });
    }

    async handleSubmit(event) { //We have to use a custom submit handler because the post_id wouldn't be included in the form data
        event.preventDefault();

        let edits = new FormData();

        edits.append("account_id", this.state.tweetEdits.account_id);
        edits.append("tweet_title", this.state.tweetEdits.tweet_title);
        edits.append("tweet_text", this.state.tweetEdits.tweet_text);

        let requestUrl = "/editTweet/" + this.state.tweetEdits.post_id;

        let request = new Request(requestUrl,
            {
                method: 'POST',
                body: edits
            });

        console.log("tweetEdits: ");
        console.log(this.state.tweetEdits);

        let res = await fetch(request);
        let currentTweet = await res.json();
        this.props.refreshTweet(currentTweet);
        this.props.onChangeTweetState('Display');
    }

    render() {

        return ( 
            <form onSubmit={this.handleSubmit}>
                <div className="TweetHeader">
                    <div className="Toggle">
                    </div>
                    <TweetTitleForm tweet_title={this.state.tweetEdits.tweet_title} handleChange={this.handleChange}/>
                </div>

                <div className="TweetContent">

                    <TwitterAccountForm account_id={this.state.tweetEdits.account_id} twitterAccounts={this.props.twitterAccounts} handleChange={this.handleChange} />
                    <TweetTextForm tweet_text={this.state.tweetEdits.tweet_text} handleChange={this.handleChange} />
                    <TweetEditMenu onChangeTweetState={this.props.onChangeTweetState}/>
                </div>

            </form> 

        );

    }
}

export default TweetEdit;
