import React from 'react';
import '../../css/tweets/TwitterAccountForm.css';

class TwitterAccountForm extends React.Component {
    constructor(props) {
        super(props);

        this.options = props.twitterAccounts.map(account => <option value={account.account_id}> {account.account_id} </option>);

        this.state = {
            selectedAccount: props.account_id
        }
    }

    handleChange(event) {
        this.setState({ selectedAccount: event.target.value });
    }

    render() {

        return (
            <div className="TwitterAccount">
                <h3>Twitter:</h3>
                <select id="TwitterAccount" name="twitterAccount" value={this.state.selectedAccount} onChange={this.handleChange}>
                     {this.options}
                </select>
            </div>
            );
    }
}

export default TwitterAccountForm;
