import React from 'react';

import LinkFirstAccount from './linkFirstAccount.js';
import FirstTweet from './firstTweet.js';
import Menu from './menu.js';
import TweetSection from './tweets/tweetSection.js';

const clone = require('rfdc')();

class LoggedIn extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            tweets: [],
            twitterAccounts: []
        };

        this.refreshTweet = this.refreshTweet.bind(this);

    }


    findTweet(post_id) {

       

    }

    refreshTweet(index, currentTweet) {

        this.currentTweets = clone(this.state.tweets);

        this.currentTweets[index] = clone(currentTweet);

        this.setState({ tweets: clone(this.currentTweets) });

        console.log("Tweets are refeshed: ");
        console.log(this.state.tweets);

    }

    componentDidMount() {
       
        fetch('/getUserTweets').then((response) => { return response.json(); }).then((data) => { console.log(data); this.setState({ tweets: data }); });
        fetch('/getLinkedAccounts').then((response) => { return response.json(); }).then((data) => { console.log(data); this.setState({ twitterAccounts: data }); });
        
    }


    render() {
        return (
            <div>
                <div className="Menubar">
                    <img src="./images/smt-logo.png" className="logo" />
                    <Menu />
                </div>
                <p>{this.state.tweets[0]}</p>
                {
                    this.state.twitterAccounts.length === 0 ? <LinkFirstAccount /> :
                    this.state.tweets.length === 0 ? <FirstTweet /> :
                    <TweetSection tweets={this.state.tweets} twitterAccounts={this.state.twitterAccounts} refreshTweet={this.refreshTweet}/>
                }
            </div>
        );
    }

}

export default LoggedIn;
