import React from 'react';
import '../../css/submenu/UnlinkAccountsMenu.css';
import Account from '../account.js';

class UnlinkAccountsMenu extends React.Component {
    constructor(props) {
        super(props);

        let linkedAccountsHelper = this.props.twitter_accounts.map((twitterAccount) => { return { account_id: twitterAccount.account_id, selected: false } });         

        this.state = { linkedAccounts: linkedAccountsHelper }

        this.select = this.select.bind(this);
    }


    select(account_id) {
        let indexOfSelected = this.state.linkedAccounts.findIndex((account) => account.account_id === account_id); 

        let editedLinkedAccounts = this.state.linkedAccounts.map((account, index) => { if (index === indexOfSelected) { account.selected = !account.selected; } return account; });

        this.setState({ linkedAccounts: editedLinkedAccounts });
    }


    render() {
        return (
            <div className="UnlinkAccountsMenu">
                <AccountList linkedAccounts={this.state.linkedAccounts} onSelect={this.select} />
                <div className="BtnContainer">
                    <div className="SubmenuBtn">Unlink Account</div>
                    <div className="SubmenuBtn" id="Cancel" onClick={this.props.changeSubmenuState}>Cancel</div>

                </div>
            </div>
        );
    }
}


function AccountList(props) {
    const linkedAccounts = props.linkedAccounts;
    const accounts = linkedAccounts.map((accountData) => <Account key={accountData.account_id} account_id={accountData.account_id} selected={accountData.selected} onSelect={props.onSelect} />);

    return (
        <div className="AccountContainer">
            {accounts}
        </div>
    );
}

export default UnlinkAccountsMenu;
