import React from 'react';
import '../../css/submenu/UnlinkAccountsMenu.css';
import './unlinkAccountConfirmMenu.js';
import Account from '../account.js';
import AccountList from '../accountList.js';

class UnlinkAccountsMenu extends React.Component {
    constructor(props) {
        super(props);

        let unlinkAccountMenuItemsHelper = props.linkedAccounts.map((twitterAccount) => { return { account_id: twitterAccount.account_id, selected: false } })

        this.state = {
            menuState: 'Select',
            unlinkAccountMenuItems: unlinkAccountMenuItemsHelper
        };

        this.handleUnlinkAccountClick = this.handleUnlinkAccountClick.bind(this);
    }


    handleUnlinkAccountClick(event) {
        if (this.props.selectedAccounts().length) this.props.changeSubmenuState(event);
    }


    select(account_id) {
        let indexOfSelected = this.state.unlinkAccountMenuItems.findIndex((account) => account.account_id === account_id);

        let unlinkAccountMenuItemsEdits = this.state.unlinkAccountMenuItems.map((account, index) => {
            if (index === indexOfSelected) { account.selected = !account.selected; }
            return account;
        });

        this.setState({ unlinkAccountMenuItems: unlinkAccountMenuItemsEdits });
    }


    getSelectedAccounts() {
        let selectedAccounts = this.state.unlinkAccountMenuItems.filter((account) => account.selected);

        return selectedAccounts;
    }


    render() {
        return (
            <div className="UnlinkAccountsMenu">
                <AccountList accounts={this.state.unlinkAccountMenuItems} onSelect={this.select} />
                <div className="BtnContainer">
                    <div className="SubmenuBtn" id="Confirm Unlink Account" onClick={this.handleUnlinkAccountClick}>Unlink Account</div>
                    <div className="SubmenuBtn" id="Settings" onClick={this.props.changeSubmenuState}>Cancel</div>
                </div>
            </div>
        );
    }
}


export default UnlinkAccountsMenu;
