import React from 'react';
import Submenu from './submenu/submenu.js';

class Menu extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            submenuState: ''
        }

        this.setSubmenuState = this.setSubmenuState.bind(this);
        this.logout = this.logout.bind(this);
    }

    changeSubmenuState(event) {
        this.setState({ submenuState: event.target.innerText });
    }

    logout() {
        let expirationDate = new Date();

        this.props.onLogout('');

        document.cookie = "username=; domain=localhost:3002; expires=" + expirationDate.toUTCString(); 
        document.cookie = "connect.sid=; domain=localhost:3002; expires=" + expirationDate.toUTCString(); 
    }

    render() {
        return (
            <div>
                <div className="Menu">
                    <div className="MenuBtn" onClick={this.logout}>Logout</div>
                    <div className="MenuBtn">Settings</div>
                    <div className="MenuBtn">Linked Accounts</div>
                </div>            
                <Submenu />
            </div>
        );
    }
}

export default Menu;
