import React from 'react';
import '../../css/login/AccountForm.css';

class LoginForm extends React.Component {
    constructor(props) {
        super(props);
        this.handleUsernameChange = this.handleUsernameChange.bind(this);
        this.handlePasswordChange = this.handlePasswordChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleLogin = this.handleLogin.bind(this);
        this.state = {
            username: '',
            password: ''
        };
    }

    handleLogin(username) {
        this.props.onLogin(username);
    }

    handleUsernameChange(event) {
        this.setState({ username: event.target.value });
    }

    handlePasswordChange(event) {
        this.setState({ password: event.target.value });
    }

    async handleSubmit(event) {
        
        let loginData = new FormData();

        loginData.append("username", this.state.username);
        loginData.append("password", this.state.password);

        let headers = new Headers();

        headers.append("Access-Control-Allow-Origin", 'http://127.0.0.1:3001/login');
        headers.append("Origin", "http://localhost:3002");
        
        let request = new Request('/login',
            {
               // headers: headers,
                method: 'POST',
                mode: 'cors',
               // credentials: 'include',
                body: loginData
            });

        //this.handleLogin('nom');

//        fetch(request).then((response) => { console.log('response');  return response.json(); }).then((data) => { this.handleLogin(data.username); console.log('bop'); });
        console.log('beginning await');
        let response = await fetch(request);
        let data = await response.json();
        this.handleLogin(data.username);
       
    }


    render() {
        
        return (
            <div className="AccountFormContainer">
                <form onSubmit={this.handleSubmit} className="AccountForm">
                    <ul>
                        <li >
                            <label for="username">Username: </label>
                            <input type="text" value={this.state.value} onChange={this.handleUsernameChange} required />
                        </li>
                        <li>
                            <label for="password">Password: </label>
                            <input type="text" value={this.state.value} onChange={this.handlePasswordChange} required />
                        </li>
                        <li>
                            <button type="submit" className="ContentBtn">Login </button>
                        </li>
                    </ul>
                </form>
                <div className="RecoverPasswordBtn">Recover Password</div>
            </div>            
        );
    }
}

export default LoginForm;
