import React from 'react';
//import logo from './logo.svg';
import './App.css';
import LoginMenu from './components/login/loginMenu.js';
import LoginForm from './components/login/loginForm.js';
import CreateAccountForm from './components/login/createAccountForm.js';

import FirstTweet from './components/firstTweet.js';
import LinkFirstAccount from './components/linkFirstAccount.js';
import Menu from './components/menu.js';
import MainCSS from './css/MainCSS.css';
import TweetSection from './components/tweets/tweetSection.js';
import Submenu from "./components/submenu/submenu.js";

class App extends React.Component {
    constructor(props) {
        super(props);
        this.handleAppStateChange = this.handleAppStateChange.bind(this);
        this.state = {
            loggedIn: false,
            user: '',
            tweets: new Array(),
            twitterAccounts: new Array(),
            appState: 'LoggingIn'
        };
    }

    handleAppStateChange(newState) {

        if (typeof newState == 'string') this.setState({ appState: newState });

    }

    render() {
        return (
            <div className="App">

                <div className="Menubar">
                    <img src="./images/smt-logo.png" className="logo" />
                    {
                        this.state.loggedIn === true ? <Menu /> :
                            this.state.appState === 'LoggingIn' ? <LoginMenu handleAppState={this.handleAppStateChange} /> : ''
                    } 
                </div>
                {
                    this.state.loggedIn ?
                        !Array.isArray(this.state.twitterAccounts) || !this.state.twitterAccounts.length ? <LinkFirstAccount /> :
                        !Array.isArray(this.state.tweets) || !this.state.tweets.length ? <FirstTweet /> :
                        <TweetSection /> :
                        this.state.appState === 'LoggingIn' ? <LoginForm /> : <CreateAccountForm handleAppState={this.handleAppStateChange} />
                }
            </div>
        );
    }
}

export default App;

