import React from 'react';
import '../../css/tweets/TweetEdit.css';
import TweetTitleForm from './tweetTitleform';
import TwitterAccountForm from './twitterAccountForm';
import TweetTextForm from './tweetTextForm.js';
import TweetEditMenu from './tweetEditMenu.js';


class TweetEdit extends React.Component {
    constructor(props) {
        super(props);
       
        //this.startingTweet = JSON.parse(JSON.stringify(this.props.tweet)); //this is so the component doesn't modify the object referenced to create the tweetEdit obj
        
        this.state = {
            tweetEdits: JSON.parse(JSON.stringify(this.props.tweet)) //this is so the component doesn't modify the object referenced to create the tweetEdit obj
        }

        this.handleTitleChange = this.handleTitleChange.bind(this);
    }

    handleTitleChange(event) {

        this.editedTweet = this.state.tweet;
        this.editedTweet.tweet_title = event.target.value;

        this.setState({ tweet: this.editedTweet });

    }

    render() {

        return ( 
            <form>
                <div className="TweetHeader">
                    <div className="Toggle">
                    </div>
                    <TweetTitleForm tweet_title={this.state.tweetEdits.tweet_title} handleTitleChange={this.handleTitleChange}/>
                </div>

                <div className="TweetContent">

                    <TwitterAccountForm account_id={this.state.tweetEdits.account_id} twitterAccounts={this.props.twitterAccounts} />
                    <TweetTextForm tweet_text={this.props.tweet.tweet_text} />
                    <TweetEditMenu onChangeTweetState={this.props.onChangeTweetState} />
                </div>

            </form> 

        );

    }
}

export default TweetEdit;
