import React from 'react';
import '../../css/tweets/TweetSection.css';
import Tweet from './tweet.js';
import TweetEdit from './tweetEdit.js';

const clone = require('rfdc')();

class TweetSection extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            tweets: props.tweets.map((tweet, index) => <Tweet tweet={tweet} twitterAccounts={this.props.twitterAccounts} index={index} refreshTweet={this.props.refreshTweet} />)
        };
    }

    componentDidUpate(prevProps) {
        console.log('componentDidUpdate Fired');
        if (this.props.tweets !== prevProps.tweets) {

            let updatedTweets = this.props.tweets.map((tweet, index) => <Tweet tweet={tweet} twitterAccounts={this.props.twitterAccounts} index={index} refreshTweet={this.props.refreshTweet} />)
            console.log("tweetSection updated");
            this.setState({ tweets: clone(updatedTweets) });
        }

    }
    

    render() {
        return (

            <div id="TweetsContainer">
                {this.state.tweets}
                <p>{this.props.tweets[0].tweet_text}</p>
                 

                
                
            </div>
            
        );
    }
}

export default TweetSection;
