import React from 'react';
import '../css/Account.css';


class Account extends React.Component {
    constructor(props) {
        super(props);

        this.handleSelect = this.handleSelect.bind(this);
    }


    handleSelect() {
        if(this.props.onSelect) this.props.onSelect(this.props.account_id);
    }


    render() {
        if (this.props.selected)
            return (<div className="AccountSelected" onClick={this.handleSelect}>{this.props.name}</div>);        
        else 
            return (<div className="Account" onClick={this.handleSelect}>{this.props.name}</div>);        
    }
}

export default Account;
