import React from 'react';
import '../../css/tweets/NewTweet.css';
import NewTweetForm from './TweetEdit';

class NewTweet extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            componentState: 'Button'
        };

    }

    async handleSubmit(event) { //We have to use a custom submit handler because the post_id wouldn't be included in the form data. 
        //We also collect the edited tweet in the database in the http response and refresh the tweets in our REACT state to reflect the new state of the database
        event.preventDefault();

        let newTweetData = new FormData();

        newTweetData.append("account_id", this.state.tweetEdits.account_id);
        newTweetData.append("tweet_title", this.state.tweetEdits.tweet_title);
        newTweetData.append("tweet_text", this.state.tweetEdits.tweet_text);
        newTweetData.append("username", this.props.username);

        let requestUrl = "/createTweet/";

        let request = new Request(requestUrl,
            {
                method: 'POST',
                body: newTweetData
            });

        await fetch(request);

        this.setState({ componentState: "Button" });
        this.props.updateTweets();
      
    }


    render() {
        return (
            <div>
                {
                    this.state.componentState === "Button" ?
                    <div className="NewTweetBtn">New Tweet</div> :
                    this.state.componentState === "CreateTweet" ?
                    <NewTweetForm tweet={{}} handleSubmit={this.handleSubmit}/> : ''

                }
            </div>
        );
    }

}

export default NewTweet;
