import React from 'react';
import '../../css/submenu/Submenu.css';
import SettingsMenu from './settingsMenu.js'
import LinkedAccountsMenu from './linkedAccountsMenu.js'
import UnlinkAccountsMenu from './unlinkAccountsMenu.js';
import DeleteAccountConfirmMenu from './deleteAccountConfirmMenu.js';
import UnlinkAccountConfirmMenu from './unlinkAccountConfirmMenu.js';

class Submenu extends React.Component {
    constructor(props) {
        super(props);
        
        this.state = {};

        this.select = this.select.bind(this);
        this.manageSubmenuState = this.manageSubmenuState.bind(this);
        this.getSelectedAccounts = this.getSelectedAccounts.bind(this);
    }


    static getDerivedStateFromProps(props, state) {
        if (
            props.twitter_accounts !== state.prevPropsTwitter_Accounts ||
            state.prevLinkedAccounts !== state.linkedAccounts
        ) {
            return {
                prevPropsTwitter_Accounts: props.twitter_accounts,
                prevLinkedAccounts: state.linkedAccounts,
                linkedAccounts: props.twitter_accounts.map((twitterAccount) => { return { account_id: twitterAccount.account_id, selected: false } })

            };
        }
        return null;

    }

    select(account_id) {
        let indexOfSelected = this.state.linkedAccounts.findIndex((account) => account.account_id === account_id);

        let editedLinkedAccounts = this.state.linkedAccounts.map((account, index) => { if (index === indexOfSelected) { account.selected = !account.selected; } return account; });

        this.setState({ linkedAccounts: editedLinkedAccounts });
    }


    getSelectedAccounts() {
        let selectedAccounts = this.state.linkedAccounts.filter((account) => account.selected);

        return selectedAccounts;
    }


    manageSubmenuState() {
        switch (this.props.currentState) {
            case 'Settings': return <SettingsMenu changeSubmenuState={this.props.changeSubmenuState} />
            case "Linked Accounts": return <LinkedAccountsMenu changeSubmenuState={this.props.changeSubmenuState} /> 
            case "Unlink Account": return <UnlinkAccountsMenu changeSubmenuState={this.props.changeSubmenuState} linkedAccounts={this.state.linkedAccounts} select={this.select} />
            case "Confirm Unlink Account": return <UnlinkAccountConfirmMenu changeSubmenuState={this.props.changeSubmenuState} selectedAccounts={this.getSelectedAccounts} />
            case "Delete User": return <DeleteAccountConfirmMenu changeSubmenuState={this.props.changeSubmenuState} />
            case "Cancel": return <SettingsMenu changeSubmenuState={this.props.changeSubmenuState} />
        }
    }


    componentDidMount() {
        let linkedAccountsHelper = this.props.twitter_accounts.map((twitterAccount) => { return ({ account_id: twitterAccount.account_id, selected: false }); });
        console.log("mounting linked:");
        console.log(this.props.twitter_accounts);
        this.setState({ linkedAccounts: linkedAccountsHelper });
    }


    render() {
        return (           
            <div className="Submenu">
                {this.manageSubmenuState()}                
            </div>         
        );
    }
}


export default Submenu;
