import React from 'react';
import '../../css/submenu/Submenu.css';
import SettingsMenu from './settingsMenu.js'
import LinkedAccountsMenu from './linkedAccountsMenu.js'
import UnlinkAccountsMenu from './unlinkAccountsMenu.js';
import DeleteAccountConfirmMenu from './deleteAccountConfirmMenu.js';
import UnlinkAccountConfirmMenu from './unlinkAccountConfirmMenu.js';

class Submenu extends React.Component {
    constructor(props) {
        super(props);

        this.manageSubmenuState = this.manageSubmenuState.bind(this);
    }

    manageSubmenuState() {
        switch (this.props.currentState) {
            case 'Settings': return <SettingsMenu changeSubmenuState={this.props.changeSubmenuState}/>
            case "Linked Accounts": return <LinkedAccountsMenu changeSubmenuState={this.props.changeSubmenuState}/> 
            case "Unlink Account": return <UnlinkAccountsMenu changeSubmenuState={this.props.changeSubmenuState}/>
            case "Delete User": return <DeleteAccountConfirmMenu changeSubmenuState={this.props.changeSubmenuState}/>
        }
    }

    render() {
        return (
            
            <div className="Submenu">
                {this.manageSubmenuState()}                
            </div>
            
        );
    }
}

export default Submenu;
