import React from 'react';
import '../../css/submenu/Submenu.css';
import SettingsMenu from './settingsMenu.js'
import LinkedAccountsMenu from './linkedAccountsMenu.js'
import UnlinkAccountsMenu from './unlinkedAccountsMenu/unlinkAccountsMenu';
import DeleteAccountConfirmMenu from './deleteAccountConfirmMenu.js';
//import UnlinkAccountConfirmMenu from './unlinkAccountConfirmMenu.js';

class Submenu extends React.Component {
    constructor(props) {
        super(props);
        
        this.state = {};

        this.manageSubmenuState = this.manageSubmenuState.bind(this);
    }


    manageSubmenuState() {
        switch (this.props.currentState) {
            case 'Settings': return <SettingsMenu changeSubmenuState={this.props.changeSubmenuState} />
            case "Linked Accounts": return <LinkedAccountsMenu changeSubmenuState={this.props.changeSubmenuState} /> 
            case "Unlink Account": return <UnlinkAccountsMenu changeSubmenuState={this.props.changeSubmenuState} linkedAccounts={this.props.twitter_accounts} select={this.select} selectedAccounts={this.getSelectedAccounts}/>
            //case "Confirm Unlink Account": return <UnlinkAccountConfirmMenu changeSubmenuState={this.props.changeSubmenuState} selectedAccounts={this.getSelectedAccounts()} />
            case "Delete User": return <DeleteAccountConfirmMenu changeSubmenuState={this.props.changeSubmenuState} />
            case "Cancel": return <SettingsMenu changeSubmenuState={this.props.changeSubmenuState} />
        }
    }


    componentDidMount() {
        let linkedAccountsHelper = this.props.twitter_accounts.map((twitterAccount) => { return ({ account_id: twitterAccount.account_id, selected: false }); });
        this.setState({ linkedAccounts: linkedAccountsHelper });
    }


    render() {
        return (           
            <div className="Submenu">
                {this.manageSubmenuState()}                
            </div>         
        );
    }
}


export default Submenu;
